@app.route('/')
def home():
    session.clear()
    return render_template('home.html')

@app.route('/<category>', methods=['GET', 'POST'])
def quiz(category):
    if category not in quizzes:
        return "Quiz non trouvé", 404

    nb_questions = 25
    # On ne tire les questions qu'à la première visite (GET)
    if request.method == 'GET' or 'quiz_data' not in session or session.get('quiz_category') != category:
        max_questions = min(nb_questions, len(quizzes[category]))
        selected_questions = random.sample(quizzes[category], max_questions)
        questions = [q.copy() for q in selected_questions]
        for q in questions:
            random.shuffle(q["options"])
        session['quiz_data'] = questions
        session['quiz_category'] = category
    else:
        questions = session['quiz_data']

    user_answers = []
    correct_answers = [q["answer"] for q in questions]
    score = 0
    show_result = False

    if request.method == 'POST':
        for i in range(len(questions)):
            ans = request.form.get(f'q{i}')
            user_answers.append(ans)
            if ans == correct_answers[i]:
                score += 1
        show_result = True
    else:
        user_answers = [None] * len(questions)

    return render_template(
        'quiz.html',
        questions=questions,
        user_answers=user_answers,
        correct_answers=correct_answers,
        score=score,
        show_result=show_result,
        category=category
    )

if __name__ == '__main__':
    app.run(debug=True)
